import { Component } from '@angular/core';
import { Title } from '@angular/platform-browser';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})
export class AppComponent {
  constructor(
    title: Title
  ) {
    title.setTitle('Prismatic Pandemonium')
  }
    deck = [
    "../assets/blue0.png",
    "../assets/red12.png",
    "../assets/red5.png",
    "../assets/green0.png",
    "../assets/green1.png",
    "../assets/blue5.png",
    "../assets/yellow4.png",
    "../assets/blue5.png",
    "../assets/blue5.png",
    "../assets/blue5.png",
    "../assets/blue5.png",
    "../assets/blue5.png",
    "../assets/blue5.png",
    "../assets/blue5.png",
    "../assets/blue5.png",
    "../assets/blue5.png",]
}

const playPileDom = document.querySelector('.play-pile')

const playerHandDom = document.querySelector('.player-hand')
// const botHandDom = document.querySelector('.player2-hand') fazer um bot

const cpuHand = []

let playPile
let cpuSCore = 0
let playerScore = 0

// rodar o jogo
let playerTurn = true
let gameOn = true
// let colorPickerIsOpen = false
let cpuDelay = Math.floor((Math.random() * cpuHand.length * 200) + 1500)
let gameOver = 100


//preload nas cartinha

const imgPreLoad: any[] = []
let preLoaded = false

const preLoadImgs = () => {
  for(let i = 0; i <= 3; i++){
    let color
    if (i === 0) color = 'red'
    if (i === 1) color = 'green'
    if (i === 2) color = 'blue'
    if (i === 3) color = 'yellow'
    for (let n = 0; n <= 14; n++){
      let img = new Image()
      img.src = '../assets/' + color + i + '.png'
      imgPreLoad.push(img)
    }
  }

  for(let i = 0; i < imgPreLoad.length; i++){
    playPileDom?.appendChild(imgPreLoad[i])
    // playPileDom.innerHTML = ''
  }
}

class Card{
  color: any;
  value: any;
  points: any;
  changeTurn: any;
  drawValue: any;
  src: any;
  constructor(rgb: string, value: number, points: number, changeTurn: boolean, drawValue: number, imgSrc: string){
    this.color = rgb
    this.value = value
    this.points = points
    this.changeTurn = changeTurn
    this.drawValue = drawValue
    this.src = imgSrc
  }
}

const deckTeste: Card[] = []

const createCard = (rgb: any, color: string) =>{
  for (let i = 0; i <= 14; i++){
    //numeros das cartas
    if(i === 0){
      deckTeste.push(new Card(rgb, i, i, true, 0, '../assets/' + color + i + '.png'))
    }
    else if (i > 0 && i <= 9){
      deckTeste.push(new Card(rgb, i, i, true, 0, '../assets/' + color + i + '.png'))
      deckTeste.push(new Card(rgb, i, i, true, 0, '../assets/' + color + i + '.png'))
    }
    //reverse/broqueia
    else if(i == 10 || i === 11){
      deckTeste.push(new Card(rgb, i, 20, false, 0, '../assets/' + color + i + '.png'))
      deckTeste.push(new Card(rgb, i, 20, false, 0, '../assets/' + color + i + '.png'))
    }
    // +2
    else if (i === 12) {
      deckTeste.push(new Card(rgb, i, 20, false, 2, '../assets/' + color + i + '.png'))
      deckTeste.push(new Card(rgb, i, 20, false, 2, '../assets/' + color + i + '.png'))
    }
    //escolher cor
    else if (i === 13) {
      deckTeste.push(new Card('any', i, 50, true, 0, '../assets/wild' + i + '.png'))
    }
    // +4
    else {
      deckTeste.push(new Card('any', i, 50, false, 4, '../assets/wild' + i + '.png'))
   }
  }
}

const createDeck = () => {
  deckTeste.length = 0

  //cria novo deck
  for(let i = 0; i <= 3; i++){
    if(i === 0){
      createCard('rgb(255, 6, 0)','red')
    }
    else if(i === 1){
      createCard('rgb(0, 170, 69)', 'green')
    }
    else if(i === 2){
      createCard('rgb(0, 150, 224)', 'blue')
    }
    else {
      createCard('rgb(255, 222, 0)', 'yellow')
    }
    console.log(deckTeste)
  }
}

const shuffleDeck = (deckTeste: string | any[]) => {
  // Método utilizado - https://www.frankmitchell.org/2015/01/fisher-yates/
  console.log('shuffling', deckTeste)  // TODO: remove

  for (let i = deckTeste.length - 1; i > 0; i--) {
      deckTeste[i].playedByPlayer = false
      let j = Math.floor(Math.random() * (i + 1))
      let temp = deckTeste[i]
      deckTeste[i] = deckTeste[j]
      deckTeste[j] = temp
  }
  console.log(deckTeste, 'shuffled') // TODO: remove
}
